TMytiObjectList = class(TtiObjectList)
private
protected
  function    GetItems(i: integer): TMytiObject; reintroduce;
  procedure   SetItems(i: integer; const AValue: TMytiObject); reintroduce;
  function    GetOwner: TMyOwner; reintroduce;
  procedure   SetOwner(const AValue: TMyOwner); reintroduce;
public
  property    Items[i:integer] : TMytiObject read GetItems write SetItems;
  procedure   Add(AObject : TMytiObject); reintroduce;
  property    Owner: TMyOwner read GetOwner write SetOwner;
published
end ;
